import React from "react";
import "../Components/Styles/About.css";

const AboutSection = () => {
  const cards = [
    {
      title: "Transactions",
      content: "Send and receive cryptocurrency with ease and without delays.",
    },
    {
      title: "High-Level Security",
      content:
        "Your assets are protected with multi-layer security and two-factor authentication.",
    },
    {
      title: "Buy and Sell",
      content:
        "A smartphone screen displaying the interface for buying and selling cryptocurrency.",
    },
  ];

  return (
    <div className="about-section">
      <h1>Transform Your Crypto Journey</h1>
      <p>Experience enhanced security, speed, and convenience with our cutting-edge cryptocurrency wallet.</p>
      <p>Created By Awsmd</p>

      <div className="card-container">
        {cards.map((card, index) => (
          <div className={`card card-${index + 1}`} key={index}>
            <div className="card-content">
              <h3>{card.title}</h3>
              <p>{card.content}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AboutSection;
