// src/Components/AuthForm.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import '../Components/Styles/AuthForm.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const AuthForm = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const userRole = sessionStorage.getItem('userRole');

    if (token && userRole) {
      if (userRole === 'admin') {
        navigate('/admin');
      } else {
        navigate('/user');
      }
    }
  }, [navigate]);

  const handleToggleSignUp = () => setIsSignUp(true);
  const handleToggleSignIn = () => setIsSignUp(false);

  const handleLoginSuccess = (token, role) => {
    sessionStorage.setItem('authToken', token);
    sessionStorage.setItem('userRole', role);

    if (role === 'admin') {
      navigate('/admin');
    } else {
      navigate('/user');
    }
  };

  return (
    <div className="auth-container-fluid d-flex justify-content-center align-items-center vh-100">
      <div className="row bg-white rounded-3 shadow-lg p-4 w-100" style={{ maxWidth: '800px' }}>
        <div className="col-md-6">
          {isSignUp ? (
            <Register onSuccess={handleLoginSuccess} />
          ) : (
            <Login onSuccess={handleLoginSuccess} />
          )}
        </div>
        <div className="col-md-6 d-flex flex-column justify-content-center align-items-center text-center bg-dark text-white">
          <h1>{isSignUp ? 'Welcome Back!' : 'Hello Friends!'}</h1>
          <p>{isSignUp ? 'Sign in with your personal details' : 'Sign up with your personal details'}</p>
          <button
            className="btn btn-light mt-3"
            onClick={isSignUp ? handleToggleSignIn : handleToggleSignUp}
          >
            {isSignUp ? 'Sign In' : 'Sign Up'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
