import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles/AuthForm.css'; // Updated path

const Login = ({ isSignUp, onSuccess }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [responseMsg, setResponseMsg] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Clear specific error when user starts typing
    setErrors({ ...errors, [name]: '' });
  };

  const validate = () => {
    let tempErrors = {};
    if (!formData.email) tempErrors.email = "Email is required";
    if (!formData.password) tempErrors.password = "Password is required";
    return tempErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/api/users/login', formData);
        setResponseMsg(response.data.message);
        sessionStorage.setItem('authToken', response.data.token); // Store token
        onSuccess(response.data.token); // Pass token to parent for navigation
      } catch (error) {
        if (error.response) {
          setErrors({ api: error.response.data.message });
        }
      }
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <form className={`p-4 ${isSignUp ? 'd-none' : ''}`} onSubmit={handleSubmit}>
      <h1 className="mb-3">Sign In</h1>
      <div className="mb-3">
        <input
          type="email"
          name="email"
          className="form-control"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        {errors.email && <div className="text-danger">{errors.email}</div>}
      </div>
      <div className="mb-3">
        <input
          type="password"
          name="password"
          className="form-control"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
        />
        {errors.password && <div className="text-danger">{errors.password}</div>}
      </div>
      <div className="mb-3">
        <a href="#" className="text-decoration-none">Forgot Your Password?</a>
      </div>
      {errors.api && <div className="text-danger">{errors.api}</div>}
      {responseMsg && <div className="text-success">{responseMsg}</div>}
      <button type="submit" className="btn btn-dark w-100">Sign In</button>
    </form>
  );
};

export default Login;
