import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Eventpage.css'; // Updated path

const EventPage = () => {
  const [isEventSetup, setIsEventSetup] = useState(false);
  const [formData, setFormData] = useState({ eventId: '' });
  const [errors, setErrors] = useState({});
  const [responseMsg, setResponseMsg] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleToggle = () => {
    setIsEventSetup(!isEventSetup);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validation for Find Event
  const validateFindEvent = () => {
    let tempErrors = {};
    if (!formData.eventId) tempErrors.eventId = "Event ID is required";
    return tempErrors;
  };

  // Handle Find Event form submission
  const handleFindEventSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateFindEvent();
    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/api/events/find-event', {
          eventId: formData.eventId
        });
        setResponseMsg(response.data.message); // Handle success response
      } catch (error) {
        if (error.response) {
          setErrors({ api: error.response.data.message });
        }
      }
    } else {
      setErrors(validationErrors);
    }
  };

  // Handle Dashboard navigation
  const goToDashboard = () => {
    navigate('/PhtographerDashboard'); // Navigate to the admin dashboard route
  };

  return (
    <div className={`container ${isEventSetup ? 'active' : ''}`} id="container">
      <div className="form-container event-setup">
        <form
          id="event-setup-form"
          className="p-4"
          onSubmit={isEventSetup ? goToDashboard : handleFindEventSubmit}
        >
          <h1 className="mb-4">{isEventSetup ? 'Dashboard' : 'Find My Photo'}</h1>
          <p className="mb-4">{isEventSetup ? 'Go to your dashboard' : 'Enter the event ID'}</p>
          {isEventSetup ? (
            <button type="button" className="btn btn-primary" onClick={goToDashboard}>
              Dashboard
            </button>
          ) : (
            <>
              <input
                type="text"
                name="eventId"
                className="form-control mb-3"
                placeholder="Event ID"
                value={formData.eventId}
                onChange={handleChange}
              />
              {errors.eventId && <div className="text-danger">{errors.eventId}</div>}
              <button type="submit" className="btn btn-primary">Find Event</button>
            </>
          )}
          {errors.api && <div className="text-danger">{errors.api}</div>}
          {responseMsg && <div className="text-success">{responseMsg}</div>}
        </form>
      </div>

      <div className="toggle-container">
        <div className="toggle">
          <div className="toggle-panel toggle-left">
            <h1>Already Have an Event?</h1>
            <p>Enter the event ID to Find the photo</p>
            <button className="btn btn-secondary" id="enter-event" onClick={handleToggle}>
              {isEventSetup ? 'Find My Photo' : 'Dashboard'}
            </button>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Organizing an Event?</h1>
            <p>Go to your dashboard</p>
            <button className="btn btn-secondary" id="setup-event" onClick={handleToggle}>
              {isEventSetup ? 'Dashboard' : 'Create Event'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventPage;
