import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [payments, setPayments] = useState([]);
  const [userCount, setUserCount] = useState(0); // New state for user count

  useEffect(() => {
    const authToken = sessionStorage.getItem('authToken');
    if (authToken) {
      fetchPayments(authToken);
      fetchUserCount(authToken); // Fetch user count
    }
  }, []);

  const fetchPayments = async (token) => {
    try {
      const response = await axios.get('http://localhost:5001/api/admin/payments', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPayments(response.data.payments);
    } catch (error) {
      console.error('Error fetching payments', error);
    }
  };

  const fetchUserCount = async (token) => {
    try {
      const response = await axios.get('http://localhost:5001/api/users/admin-data', {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log(response.data); // Add this to inspect the response
      setUserCount(response.data.userCount); // Make sure response contains userCount
    } catch (error) {
      console.error('Error fetching user count', error);
    }
  };


  return (
    <div className="dashboardcontainer">
      <header className="dashboard-header">
        <h1>Admin Analytics</h1>
      </header>
      <section className="dashboard-stats">
        <div className="stat-card">
          <h2>Total Users</h2>
          <p>{userCount}</p> {/* Display the real user count */}
        </div>
        <div className="stat-card">
          <h2>Total Payments</h2>
          <p>$8,459</p>
        </div>
        <div className="stat-card">
          <h2>Best Plan</h2>
          <p>Pro Plan - $12.99/month</p>
        </div>
      </section>
      <section className="payment-tracking">
        <h2>Recent Payments</h2>
        <ul>
          {payments.map(payment => (
            <li key={payment._id}>
              {payment.userEmail} - {payment.planName}: ${payment.planPrice} ({payment.status})
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default AdminDashboard;
