import React, { useState } from 'react';
import { uploadImages } from '../../util/uploadImages';
import { toast } from 'react-toastify';

const PhotoUpload = () => {
  const [selectedImages, setSelectedImages] = useState([]);
  const [name, setName] = useState('');
  const [eventName, setEventName] = useState('');

  const handleImageChange = (event) => {
    if (event.target.files) {
      setSelectedImages([...event.target.files]);
    }
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEventNameChange = (event) => {
    setEventName(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (selectedImages.length === 0) {
      toast.error("No images selected");
      return;
    }

    if (!name || !eventName) {
      toast.error("Name and Event Name are required");
      return;
    }

    try {
      const eventID = 'some_event_id'; // Replace this with actual event ID

      const imageUrls = await uploadImages(selectedImages, eventID);
      if (imageUrls.length > 0) {
        toast.success("Images uploaded successfully");
        console.log("Uploaded image URLs:", imageUrls);
        console.log("Name:", name);
        console.log("Event Name:", eventName);
      } else {
        toast.error("Failed to upload images");
      }
    } catch (error) {
      toast.error("An error occurred while uploading images");
    }
  };

  return (
    <div className="photo-upload-container">
      <h1>Create New Event & Upload Photos</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input 
            type="text" 
            id="name" 
            value={name} 
            onChange={handleNameChange} 
            placeholder="Enter your name" 
          />
        </div>
        <div>
          <label htmlFor="eventName">Event Name:</label>
          <input 
            type="text" 
            id="eventName" 
            value={eventName} 
            onChange={handleEventNameChange} 
            placeholder="Enter the event name" 
          />
        </div>
        <div>
          <input 
            type="file" 
            multiple 
            accept="image/*" 
            onChange={handleImageChange} 
          />
        </div>
        <button type="submit">Upload Images</button>
      </form>
    </div>
  );
};

export default PhotoUpload;
