import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import '../Components/Styles/AuthForm.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const AuthForm = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const navigate = useNavigate();

  const handleToggleSignUp = () => setIsSignUp(true);
  const handleToggleSignIn = () => setIsSignUp(false);

  // Navigate to EventPage on successful login/register
 // Updated handleLoginSuccess
const handleLoginSuccess = (token) => {
  sessionStorage.setItem('authToken', token);
  navigate('/event');
};


  return (
    <div className="auth-container-fluid d-flex justify-content-center align-items-center vh-100">
      <div className="row bg-white rounded-3 shadow-lg p-4 w-100" style={{ maxWidth: '800px' }}>
        <div className={`col-md-6 ${isSignUp ? 'order-md-2' : ''}`}>
          <Register isSignUp={isSignUp} onSuccess={handleLoginSuccess} />
          <Login isSignUp={isSignUp} onSuccess={handleLoginSuccess} />
        </div>
        <div className="col-md-6 d-flex flex-column justify-content-center align-items-center text-center bg-dark text-white">
          <h1>{isSignUp ? 'Welcome Back!' : 'Hello Friends!'}</h1>
          <p>{isSignUp ? 'Sign in with your personal details' : 'Sign up with your personal details'}</p>
          <button
            className="btn btn-light mt-3"
            onClick={isSignUp ? handleToggleSignIn : handleToggleSignUp} 
          >
            {isSignUp ? 'Sign In' : 'Sign Up'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
