import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Correct hook for React Router v6
import "./Payment.css";

const PaymentPage = () => {
  const navigate = useNavigate();

  const [error, setError] = useState(null); // State for managing errors

  const plans = [
    {
      name: 'Pro',
      price: '16.58',
      storage: '100GB',
      galleryFeatures: [
        'Face Searches: Unlimited',
        'Registrations: Unlimited',
        'PicReco Domain',
        '1 Branding',
        'Limited PicReco Branding',
      ],
      notifications: [
        'Email Unlimited (from PicReco Domain)',
        'WhatsApp AddOn (from PicReco Number)',
        'WhatsApp Credits: 100 Included',
      ],
    },
    {
      name: 'Ultra',
      price: '24.91',
      storage: '500GB',
      galleryFeatures: [
        'Face Searches: Unlimited',
        'Registrations: Unlimited',
        'Custom Sub-Domain',
        '1 Branding',
        'Limited PicReco Branding',
      ],
      notifications: [
        'Email Unlimited (from PicReco Domain)',
        'WhatsApp AddOn (from PicReco Number)',
        'WhatsApp Credits: 500 Included',
      ],
    },
    {
      name: 'Promax',
      price: '31.58',
      storage: '1TB',
      galleryFeatures: [
        'Face Searches: Unlimited',
        'Registrations: Unlimited',
        'Your Own Domain',
        '2 Branding',
        'No PicReco Branding',
      ],
      notifications: [
        'Email Unlimited (from PicReco Domain)',
        'WhatsApp AddOn (from PicReco Number)',
        'WhatsApp Credits: 1000 Included',
      ],
    },
  ];

  useEffect(() => {
    const checkPaymentStatus = async () => {
      try {
        const response = await axios.get('/api/payments/status');
        if (response.data.isPaid) {
          navigate('/photographer-dashboard');
        }
      } catch (error) {
        setError('Error checking payment status. Please try again later.');
        console.error('Error checking payment status:', error);
      }
    };

    checkPaymentStatus();
  }, [navigate]);

  const handleSelectPlan = async (planName, planPrice) => {
    try {
      const response = await axios.post('http://localhost:3000/api/payments/create-order', {
        planName,
        planPrice,
      });
      window.location.href = response.data.approvalUrl;
    } catch (error) {
      setError('Error creating order. Please try again later.');
      console.error('Error creating order:', error);
    }
  };

  return (
    <div className="payment-page">
      <h2>Upgrade Plan</h2>
      {error && <p className="error-message">{error}</p>} {/* Display error messages */}
      <div className="plans-contain">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p className="price">${plan.price} Per Month</p>
            <p className="storage">Storage: {plan.storage}</p>
            <div className="features">
              <h4>Gallery</h4>
              <ul>
                {plan.galleryFeatures.map((feature, i) => (
                  <li key={i}>{feature}</li>
                ))}
              </ul>
            </div>
            <div className="notifications">
              <h4>Notifications</h4>
              <ul>
                {plan.notifications.map((notification, i) => (
                  <li key={i}>{notification}</li>
                ))}
              </ul>
            </div>
            <button onClick={() => handleSelectPlan(plan.name, plan.price)}>Select</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PaymentPage;
