// UserManagement.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserManagement.css'; // Create and style this file


const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [showPaidOnly, setShowPaidOnly] = useState(false);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = sessionStorage.getItem('authToken');
        const response = await axios.get('http://localhost:5001/api/users/admin-data', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsers(response.data.users);
        setFilteredUsers(response.data.users); // Initially set all users as filtered users
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleFilterChange = () => {
    setShowPaidOnly(!showPaidOnly);
    if (!showPaidOnly) {
      const paidPhotographers = users.filter(user => user.isPaid && user.role === 'photographer');
      setFilteredUsers(paidPhotographers);
    } else {
      setFilteredUsers(users);
    }
  };

  const handleDelete = async (userId) => {
    try {
      const token = sessionStorage.getItem('authToken');
      await axios.delete(`http://localhost:5001/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter(user => user._id !== userId));
      setFilteredUsers(filteredUsers.filter(user => user._id !== userId));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleBlockToggle = async (userId) => {
    try {
      const token = sessionStorage.getItem('authToken');
      const user = users.find(user => user._id === userId);
      const newBlockStatus = !user.isBlocked;
      const response = await axios.put(`http://localhost:5001/api/users/block/${userId}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const updatedUser = response.data.user;

      // Update state to reflect new blocked status
      const updatedUsers = users.map(user =>
        user._id === userId ? updatedUser : user
      );
      setUsers(updatedUsers);
      setFilteredUsers(updatedUsers);
    } catch (error) {
      console.error('Error updating block status:', error);
    }
  };

  return (
    <div className="user-management-container">
      <h1>User Management</h1>

      {/* Filter Checkbox */}
      <label>
        <input
          type="checkbox"
          checked={showPaidOnly}
          onChange={handleFilterChange}
        />
        Show Paid Photographers Only
      </label>

      <div className="user-cards">
        {filteredUsers.map(user => (
          <div className="user-card" key={user._id}>
            <h2>{user.name}</h2>
            <p>Email: {user.email}</p>
            <p>Role: {user.role}</p>
            <p>Plan: {user.planName}</p>
            <p>Payment Status: {user.isPaid ? 'Paid' : 'Unpaid'}</p>
            <p>Blocked: {user.isBlocked ? 'Yes' : 'No'}</p>
            <button onClick={() => handleDelete(user._id)}>Delete</button>
            <button onClick={() => handleBlockToggle(user._id)}>
              {user.isBlocked ? 'Unblock' : 'Block'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserManagement;
