// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './Components/Home';
import About from './Components/LandingPage/About';
import AuthForm from './Components/AuthForm';
import AdminDashboard from './Components/AdminDashboard';
import UserDashboard from './Components/UserDashboard';
import EventPage from './Components/Eventpage';

import './App.css';

// PrivateRoute component to protect routes based on the user's role
const PrivateRoute = ({ children, role }) => {
  const userRole = sessionStorage.getItem('userRole');
  return userRole === role ? children : <Navigate to="/auth" />;
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/auth" element={<AuthForm />} />
        <Route path="/admin" element={<PrivateRoute role="admin"><AdminDashboard /></PrivateRoute>} />
        <Route path="/user" element={<PrivateRoute role="user"><EventPage /></PrivateRoute>} />
        <Route path="/user-dashboard" element={<UserDashboard />} />      </Routes>
    </Router>
  );
}

export default App;
