import React, { useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "./Payment.css";

const PaymentPage = () => {
  const navigate = useNavigate();

  const plans = [
    // Plan data remains the same...
  ];

  useEffect(() => {
    const checkPaymentStatus = async () => {
      try {
        const response = await axios.get('/api/payment/status');
        if (response.data.isPaid) {
          navigate('/photographer-dashboard');
        }
      } catch (error) {
        console.error('Error checking payment status:', error);
      }
    };

    checkPaymentStatus();
  }, [navigate]);

  const handleSelectPlan = async (planName, planPrice) => {
    try {
      const response = await axios.post('/api/payment/create-order', {
        planName,
        planPrice,
      });
      window.location.href = response.data.approvalUrl;
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  return (
    <div className="payment-page">
      <h2>Upgrade Plan</h2>
      <div className="plans-contain">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p className="price">${plan.price} Per Month</p>
            <p className="storage">Storage: {plan.storage}</p>
            <div className="features">
              <h4>Gallery</h4>
              <ul>
                {plan.galleryFeatures.map((feature, i) => (
                  <li key={i}>{feature}</li>
                ))}
              </ul>
            </div>
            <div className="notifications">
              <h4>Notifications</h4>
              <ul>
                {plan.notifications.map((notification, i) => (
                  <li key={i}>{notification}</li>
                ))}
              </ul>
            </div>
            <button onClick={() => handleSelectPlan(plan.name, plan.price)}>Select</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PaymentPage;
