import React, { useState, useEffect } from 'react';
import './Payment.css';

const PaymentPage = () => {
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [paypalLoaded, setPaypalLoaded] = useState(false);

  // State to store selected plan details
  const [selectedPlanDetails, setSelectedPlanDetails] = useState({
    name: '',
    price: ''
  });

  // Define PayPal plan IDs
  const plans = [
    { name: 'Pro', price: '16.58', storage: '100GB', planId: 'P-0LX228426N2348600M3QPB3Q' }, // Use actual PayPal Plan ID here
    { name: 'Ultra', price: '24.91', storage: '500GB', planId: 'P-0LX228426N2348600M3QPB3Q' }, // Use actual PayPal Plan ID here
    { name: 'Promax', price: '31.58', storage: '1TB', planId: 'P-0LX228426N2348600M3QPB3Q' }, // Use actual PayPal Plan ID here
  ];

  useEffect(() => {
    // Load PayPal SDK script dynamically
    const script = document.createElement('script');
    script.src = 'https://www.paypal.com/sdk/js?client-id=ARuLPQZ6fA9iKKCu-YyCrFX5OpGJj92bvhHXLFIADCr6q3IU9MM6ILvYhhyYlrJuuZZa5MDm3JRIlt5R&vault=true&intent=subscription';
    script.async = true;
    script.onload = () => setPaypalLoaded(true);
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script); // Clean up script on component unmount
    };
  }, []);

  const renderPayPalButton = (planId) => {
    if (!paypalLoaded) return;

    // Remove previous PayPal button if exists
    const previousButton = document.getElementById('paypal-button-container');
    if (previousButton) {
      previousButton.innerHTML = '';
    }

    window.paypal.Buttons({
      style: {
        shape: 'pill',
        color: 'silver',
        layout: 'horizontal',
        label: 'subscribe'
      },
      createSubscription: function (data, actions) {
        return actions.subscription.create({
          plan_id: planId // Use the selected plan ID
        });
      },
      onApprove: function (data, actions) {
        alert('Subscription created successfully! Subscription ID: ' + data.subscriptionID);
        // Optionally, handle post-approval actions (e.g., update the backend)
      },
      onError: function (err) {
        console.error('PayPal Error:', err);
      }
    }).render('#paypal-button-container'); // Render PayPal button in the container
  };

  const handleSelectPlan = (plan) => {
    setSelectedPlan(plan.planId);
    setSelectedPlanDetails({
      name: plan.name,
      price: plan.price
    });
    console.log(`Selected plan ID: ${plan.planId}`); // Debugging statement

    // Automatically render and open the PayPal subscription modal
    renderPayPalButton(plan.planId);
    document.getElementById('paypal-button-container').scrollIntoView(); // Scroll to PayPal button container
  };

  return (
    <div className="payment-page">
      <h2>Select a Plan</h2>
      <div className="plans-container">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p>${plan.price} per month</p>
            <p>{plan.storage} Storage</p>
            <button onClick={() => handleSelectPlan(plan)}>
              Select {plan.name}
            </button>
          </div>
        ))}
      </div>

      {/* Display selected plan details */}
      {selectedPlan && (
        <div className="selected-plan-details">
          <h3>Selected Plan</h3>
          <p><strong>Name:</strong> {selectedPlanDetails.name}</p>
          <p><strong>Price:</strong> ${selectedPlanDetails.price}</p>
        </div>
      )}

      {/* PayPal button container */}
      <div id="paypal-button-container"></div>
    </div>
  );
};

export default PaymentPage;
