import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Payment.css';

const PaymentPage = () => {
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const plans = [
    { name: 'Pro', price: '16.58', storage: '100GB', planId: 'PLAN_ID_PRO' },
    { name: 'Ultra', price: '24.91', storage: '500GB', planId: 'PLAN_ID_ULTRA' },
    { name: 'Promax', price: '31.58', storage: '1TB', planId: 'PLAN_ID_PROMAX' },
  ];

  // Load the PayPal script when the component mounts
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://www.paypal.com/sdk/js?client-id=ARuLPQZ6fA9iKKCu-YyCrFX5OpGJj92bvhHXLFIADCr6q3IU9MM6ILvYhhyYlrJuuZZa5MDm3JRIlt5R&vault=true&intent=subscription';
    script.async = true;
    script.onload = () => {
      console.log('PayPal SDK loaded successfully');
      renderPayPalButton();
    };
    script.onerror = (e) => {
      console.error('Error loading PayPal SDK:', e);
    };
    document.body.appendChild(script);
  }, []);

  // Render the PayPal button after the script is loaded
  const renderPayPalButton = () => {
    if (!selectedPlan) return;

    window.paypal.Buttons({
      style: {
        shape: 'pill',
        color: 'silver',
        layout: 'horizontal',
        label: 'subscribe',
      },
      createSubscription: function (data, actions) {
        return actions.subscription.create({
          plan_id: selectedPlan,
        });
      },
      onApprove: function (data, actions) {
        alert(`Subscription successful! ID: ${data.subscriptionID}`);
      },
      onError: function (err) {
        console.error('Error during PayPal transaction:', err);
        setError('An error occurred during the PayPal transaction.');
      },
    }).render('#paypal-button-container');
  };

  // Handle plan selection
  const handleSelectPlan = (planId) => {
    setSelectedPlan(planId);
    console.log(`Selected plan ID: ${planId}`); // Debugging statement
  };

  // Handle payment (PayPal subscription)
  const handlePayment = async () => {
    if (!selectedPlan) {
      alert('Please select a plan first!');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('authToken');
      if (!token) {
        throw new Error('Authorization token not found');
      }

      const response = await axios.post(
        'http://localhost:5001/api/payments/create-subscription',
        { planId: selectedPlan },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const approvalUrl = response.data.approvalUrl;
      if (approvalUrl) {
        window.location.href = approvalUrl;
      } else {
        throw new Error('Approval URL not found in response');
      }
    } catch (error) {
      console.error('Error during payment:', error);
      setError(error.message || 'Failed to create the payment. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-page">
      <h2>Select a Plan</h2>
      <div className="plans-container">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p>${plan.price} per month</p>
            <p>{plan.storage} Storage</p>
            <button onClick={() => handleSelectPlan(plan.planId)}>
              Select {plan.name}
            </button>
          </div>
        ))}
      </div>

      {error && <p className="error-message">{error}</p>}

      <div id="paypal-button-container"></div>

      <button
        onClick={handlePayment}
        disabled={!selectedPlan || loading}
      >
        {loading ? 'Processing...' : 'Proceed to PayPal'}
      </button>
    </div>
  );
};

export default PaymentPage;
