export const uploadImages = async (images, name, eventName) => {
  const formData = new FormData();

  // Append name and eventName to formData
  formData.append('name', name);
  formData.append('eventName', eventName);

  // Append selected images to formData
  for (const image of images) {
    formData.append('photos', image);  // 'photos' is the key expected by the backend
  }

  try {
    const eventID = 'someEventID'; // Replace with actual eventID or make it dynamic

    // Retrieve the JWT token (assuming it's stored in localStorage)
    const token = localStorage.getItem('token'); // Or get it from wherever you're storing it

    const response = await fetch(`http://localhost:5001/api/photos/upload/${eventID}`, {
      method: "POST",
      headers: {
        'Authorization': `Bearer ${token}`,  // Add the JWT token to the Authorization header
      },
      body: formData,
    });

    if (response.ok) {
      const data = await response.json();
      console.log("Images uploaded successfully:", data);
      return data.imageUrls; // Assuming the API returns an array of image URLs
    } else {
      console.error("Failed to upload the images.");
      return [];
    }
  } catch (error) {
    console.error("Error:", error);
    return [];
  }
};
