import React from "react";
import { Carousel, Row, Col } from "react-bootstrap";
import "../";

const AboutSection = () => {
  const cards = [
    {
      title: "Transactions",
      content: "Send and receive cryptocurrency with ease and without delays.",
      image: "path_to_transaction_image", // Replace with your image path
    },
    {
      title: "High-Level Security",
      content:
        "Your assets are protected with multi-layer security and two-factor authentication.",
      image: "path_to_security_image", // Replace with your image path
    },
    {
      title: "Buy and Sell",
      content:
        "A smartphone screen displaying the interface for buying and selling cryptocurrency.",
      image: "path_to_buy_sell_image", // Replace with your image path
    },
  ];

  return (
    <div className="about-section">
      <h1>Transform Your Crypto Journey</h1>
      <p>
        Experience enhanced security, speed, and convenience with our
        cutting-edge cryptocurrency wallet.
      </p>
      <p>Created By Awsmd</p>

      <Carousel interval={null} indicators={false}>
        {cards.map((card, index) => (
          <Carousel.Item key={index}>
            <Row className="justify-content-center">
              <Col md={4}>
                <div className="card-hover">
                  <img
                    src={card.image}
                    alt={card.title}
                    className="card-image"
                  />
                  <div className="card-details">
                    <h3>{card.title}</h3>
                    <p>{card.content}</p>
                  </div>
                </div>
              </Col>
            </Row>
          </Carousel.Item>
        ))}
      </Carousel>
    </div>
  );
};

export default AboutSection;
