import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios';
import './Eventpage.css';

const EventPage = () => {
  const [isEventSetup, setIsEventSetup] = useState(false); // Toggle between 'Find Event' and 'Create Event'
  const [formData, setFormData] = useState({ eventId: '', eventName: '', eventDate: '', photos: [] });
  const [errors, setErrors] = useState({});
  const [responseMsg, setResponseMsg] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  // Handle toggle between event setup modes
  const handleToggle = () => {
    setIsEventSetup(!isEventSetup);
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle photo file changes
  const handleFileChange = (e) => {
    setFormData({ ...formData, photos: e.target.files });
  };

  // Validation for Find Event
  const validateFindEvent = () => {
    let tempErrors = {};
    if (!formData.eventId) tempErrors.eventId = "Event ID is required";
    return tempErrors;
  };

  // Validation for Create Event
  const validateCreateEvent = () => {
    let tempErrors = {};
    if (!formData.eventName) tempErrors.eventName = "Event Name is required";
    if (!formData.eventDate) tempErrors.eventDate = "Event Date is required";
    if (formData.photos.length === 0) tempErrors.photos = "At least one photo is required";
    return tempErrors;
  };

  // Handle Find Event form submission
  const handleFindEventSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateFindEvent();
    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/api/events/find-event', {
          eventId: formData.eventId
        });
        setResponseMsg(response.data.message); // Handle success response
        navigate('#'); // Navigate to the found event page
      } catch (error) {
        if (error.response) {
          setErrors({ api: error.response.data.message });
        }
      }
    } else {
      setErrors(validationErrors);
    }
  };

  // Handle Create Event form submission
  const handleCreateEventSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateCreateEvent();
    if (Object.keys(validationErrors).length === 0) {
      const eventFormData = new FormData();
      eventFormData.append('eventName', formData.eventName);
      eventFormData.append('eventDate', formData.eventDate);
      for (let i = 0; i < formData.photos.length; i++) {
        eventFormData.append('photos', formData.photos[i]);
      }
      try {
        const response = await axios.post('http://localhost:5001/api/events/create-event', eventFormData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setResponseMsg('Event created successfully!');
        navigate('/dashboard');
      } catch (error) {
        if (error.response) {
          setErrors({ api: error.response.data.message });
        }
      }
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <div className={`cont ${isEventSetup ? 'active' : ''}`} id="container">
      <div className="form-container event-setup">
        <form
          id="event-setup-form"
          className="p-4"
          onSubmit={isEventSetup ? handleCreateEventSubmit : handleFindEventSubmit}
        >
          <h1 className="mb-4">{isEventSetup ? 'Create New Event' : 'Find My Photo'}</h1>
          <p className="mb-4">{isEventSetup ? 'Create a new event and upload photos' : 'Enter the event ID'}</p>

          {isEventSetup ? (
            <>
              <input
                type="text"
                name="eventName"
                className="form-control mb-3"
                placeholder="Event Name"
                value={formData.eventName}
                onChange={handleChange}
              />
              {errors.eventName && <div className="text-danger">{errors.eventName}</div>}

              <input
                type="date"
                name="eventDate"
                className="form-control mb-3"
                value={formData.eventDate}
                onChange={handleChange}
              />
              {errors.eventDate && <div className="text-danger">{errors.eventDate}</div>}

              <input
                type="file"
                name="photos"
                className="form-control mb-3"
                multiple
                onChange={handleFileChange}
              />
              {errors.photos && <div className="text-danger">{errors.photos}</div>}

              <button type="submit" className="btn btn-primary">Create Event</button>
            </>
          ) : (
            <>
              <input
                type="text"
                name="eventId"
                className="form-control mb-3"
                placeholder="Event ID"
                value={formData.eventId}
                onChange={handleChange}
              />
              {errors.eventId && <div className="text-danger">{errors.eventId}</div>}

              <button type="submit" className="btn btn-primary">Find Event</button>
            </>
          )}

          {errors.api && <div className="text-danger">{errors.api}</div>}
          {responseMsg && <div className="text-success">{responseMsg}</div>}
        </form>
      </div>

      <div className="toggle-container">
        <div className="toggle">
          <div className="toggle-panel toggle-left">
            <h1>Already Have an Event?</h1>
            <p>Enter the event ID to find photos</p>
            <button className="btn btn-secondary" id="enter-event" onClick={handleToggle}>
              {isEventSetup ? 'Find My Photo' : 'Create Event'}
            </button>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Organizing an Event?</h1>
            <p>Go to your dashboard or create a new event</p>
            <button className="btn btn-secondary" id="setup-event" onClick={handleToggle}>
              {isEventSetup ? 'Dashboard' : 'Find My Photo'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventPage;
