import React, { useState } from 'react';
import { uploadImages } from '../../util/uploadImages'; // Ensure this utility is correctly implemented
import { toast } from 'react-toastify';

const PhotoUpload = () => {
  const [selectedImages, setSelectedImages] = useState([]);
  const [name, setName] = useState('');
  const [eventName, setEventName] = useState('');

  // Handle image file selection
  const handleImageChange = (event) => {
    if (event.target.files) {
      setSelectedImages([...event.target.files]);
    }
  };

  // Handle input change for Name
  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  // Handle input change for Event Name
  const handleEventNameChange = (event) => {
    setEventName(event.target.value);
  };

  // Handle form submission and image upload
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (selectedImages.length === 0) {
      toast.error("No images selected");
      return;
    }

    if (!name || !eventName) {
      toast.error("Name and Event Name are required");
      return;
    }

    try {
      // Call the upload function and pass selected images
      const imageUrls = await uploadImages(selectedImages, name, eventName);  // Assuming this utility uploads images to the server
      if (imageUrls.length > 0) {
        toast.success("Images uploaded successfully");
        console.log("Uploaded image URLs:", imageUrls);
        console.log("Name:", name);
        console.log("Event Name:", eventName);
      } else {
        toast.error("Failed to upload images");
      }
    } catch (error) {
      toast.error("An error occurred while uploading images");
    }
  };

  return (
    <div>
      <h2>Create Event and Upload Photos</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Your Name:</label>
          <input 
            type="text" 
            id="name" 
            value={name} 
            onChange={handleNameChange} 
            placeholder="Enter your name" 
          />
        </div>

        <div>
          <label htmlFor="eventName">Event Name:</label>
          <input 
            type="text" 
            id="eventName" 
            value={eventName} 
            onChange={handleEventNameChange} 
            placeholder="Enter the event name" 
          />
        </div>

        <div>
          <label htmlFor="imageUpload">Select Images:</label>
          <input 
            type="file" 
            multiple 
            accept="image/*" 
            onChange={handleImageChange} 
          />
        </div>

        <button type="submit">Upload Images</button>
      </form>
    </div>
  );
};

export default PhotoUpload;
