import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import Login from "./Login";
import Register from "./Register";
import "./AuthForm.css";
import SignUpImage from 'picrego/Frontend/src/assets/Signup.png';  
import SignInImage from 'picrego/Frontend/src/assets/Mobile login-rafiki.png'; 


const AuthForm = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const navigate = useNavigate();

  const handleToggleSignUp = () => setIsSignUp(true);
  const handleToggleSignIn = () => setIsSignUp(false);

  const handleLoginSuccess = (token, role) => {
    sessionStorage.setItem('authToken', token);
    sessionStorage.setItem('userRole', role);

    if (role === 'admin') {
      navigate('/admin');
    } else {
      navigate('/user');
    }
  };

  return (
    <div className={`main ${isSignUp ? "sign-up-mode" : ""}`}>
      <div className="forms-container">
        <div className="signin-signup">
          {isSignUp ? (
            <Register onSuccess={handleLoginSuccess} />
          ) : (
            <Login onSuccess={handleLoginSuccess} />
          )}
        </div>
      </div>

      <div className="panels-container">
        <div className="panel left-panel">
          <div className="content">
            <h3>New here?</h3>
            <p>Sign up to explore our event photo management system!</p>
            <button className="btn transparent" onClick={handleToggleSignUp}>
              Sign Up
            </button>
          </div>
          <img src={SignUPImage} className="image" alt="Sign In" />
          </div>
        <div className="panel right-panel">
          <div className="content">
            <h3>One of us?</h3>
            <p>Sign in to continue accessing your event photos!</p>
            <button className="btn transparent" onClick={handleToggleSignIn}>
              Sign In
            </button>
          </div>
          <img src={SignInImage} className="image" alt="Sign In" />
          </div>
      </div>
    </div>
  );
};

export default AuthForm;
