import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Eventpage.css';

const EventPage = () => {
  const [isEventSetup, setIsEventSetup] = useState(false); // Default to entering event ID/QR
  const [formData, setFormData] = useState({ eventName: '', eventDate: '', eventId: '' });
  const [errors, setErrors] = useState({});
  const [responseMsg, setResponseMsg] = useState('');

  const handleToggle = () => {
    setIsEventSetup(!isEventSetup);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validation for Create Event
  const validateCreateEvent = () => {
    let tempErrors = {};
    if (!formData.eventName) tempErrors.eventName = "Event Name is required";
    if (!formData.eventDate) tempErrors.eventDate = "Event Date is required";
    return tempErrors;
  };

  // Validation for Find Event
  const validateFindEvent = () => {
    let tempErrors = {};
    if (!formData.eventId) tempErrors.eventId = "Event ID is required";
    return tempErrors;
  };

  // Handle Create Event form submission
  const handleCreateEventSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateCreateEvent();
    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/api/events/create-event', {
          eventName: formData.eventName,
          eventDate: formData.eventDate
        });
        setResponseMsg(response.data.message); // Handle success response
      } catch (error) {
        if (error.response) {
          setErrors({ api: error.response.data.message });
        }
      }
    } else {
      setErrors(validationErrors);
    }
  };

  // Handle Find Event form submission
  const handleFindEventSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateFindEvent();
    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:5001/api/events/find-event', {
          eventId: formData.eventId
        });
        setResponseMsg(response.data.message); // Handle success response
      } catch (error) {
        if (error.response) {
          setErrors({ api: error.response.data.message });
        }
      }
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <div className={`container ${isEventSetup ? 'active' : ''}`} id="container">
      <div className="form-container event-setup">
        <form
          id="event-setup-form"
          className="p-4"
          onSubmit={isEventSetup ? handleCreateEventSubmit : handleFindEventSubmit}
        >
          <h1 className="mb-4">{isEventSetup ? 'Create Event' : 'Find My Photo'}</h1>
          <p className="mb-4">{isEventSetup ? 'Enter event details to set up a new event' : 'Enter the event ID'}</p>
          {isEventSetup ? (
            <>
              <input
                type="text"
                name="eventName"
                className="form-control mb-3"
                placeholder="Event Name"
                value={formData.eventName}
                onChange={handleChange}
              />
              {errors.eventName && <div className="text-danger">{errors.eventName}</div>}
              <input
                type="date"
                name="eventDate"
                className="form-control mb-3"
                placeholder="Event Date"
                value={formData.eventDate}
                onChange={handleChange}
              />
              {errors.eventDate && <div className="text-danger">{errors.eventDate}</div>}
              <button type="submit" className="btn btn-primary">Create Event</button>
            </>
          ) : (
            <>
              <input
                type="text"
                name="eventId"
                className="form-control mb-3"
                placeholder="Event ID"
                value={formData.eventId}
                onChange={handleChange}
              />
              {errors.eventId && <div className="text-danger">{errors.eventId}</div>}
              <button type="submit" className="btn btn-primary">Find Event</button>
            </>
          )}
          {errors.api && <div className="text-danger">{errors.api}</div>}
          {responseMsg && <div className="text-success">{responseMsg}</div>}
        </form>
      </div>

      <div className="toggle-container">
        <div className="toggle">
          <div className="toggle-panel toggle-left">
            <h1>Already Have an Event?</h1>
            <p>Enter the event ID to Find the photo</p>
            <button className="btn btn-secondary" id="enter-event" onClick={handleToggle}>
              {isEventSetup ? 'Find My Photo' : 'Create Event'}
            </button>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Organizing an Event?</h1>
            <p>Set up your event and invite participants</p>
            <button className="btn btn-secondary" id="setup-event" onClick={handleToggle}>
              {isEventSetup ? 'Create Event' : 'Find My Photo'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventPage;
