import React, { useState } from 'react';
import axios from 'axios';
import './Payment.css';

const PaymentPage = () => {
  const [selectedPlan, setSelectedPlan] = useState(null); // State to store the selected plan
  const [loading, setLoading] = useState(false); // Loading state while processing the payment
  const [error, setError] = useState(null); // State to store any errors

  const plans = [
    {
      name: 'Pro',
      price: '16.58',
      storage: '100GB',
      planId: 'PLAN_ID_PRO', // Replace with your PayPal plan ID
    },
    {
      name: 'Ultra',
      price: '24.91',
      storage: '500GB',
      planId: 'PLAN_ID_ULTRA', // Replace with your PayPal plan ID
    },
    {
      name: 'Promax',
      price: '31.58',
      storage: '1TB',
      planId: 'PLAN_ID_PROMAX', // Replace with your PayPal plan ID
    },
  ];

  const handleSelectPlan = (planId) => {
    setSelectedPlan(planId); // Set the selected plan when user clicks on a plan
  };

  const handlePayment = async () => {
    if (!selectedPlan) {
      alert('Please select a plan first!');
      return;
    }

    try {
      setLoading(true); // Show loading while processing payment
      setError(null); // Clear any previous errors

      // Send request to your server to create a PayPal order or subscription
      const token = localStorage.getItem('authToken'); // Retrieve token from localStorage
      const response = await axios.post(
        'http://localhost:5001/api/payments/create-subscription', // Replace with your actual backend API
        { planId: selectedPlan }, // Send selected plan to backend
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include authorization token in headers
          },
        }
      );

      // Redirect the user to PayPal approval URL
      const approvalUrl = response.data.approvalUrl; // Extract PayPal approval URL from the response
      window.location.href = approvalUrl; // Redirect to PayPal page
    } catch (error) {
      console.error('Error during payment:', error);
      setError('Failed to create the payment. Please try again.');
    } finally {
      setLoading(false); // Stop loading once the process is complete
    }
  };

  return (
    <div className="payment-page">
      <h2>Select a Plan</h2>
      <div className="plans-container">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p>${plan.price} per month</p>
            <p>{plan.storage} Storage</p>
            <button onClick={() => handleSelectPlan(plan.planId)}>
              Select {plan.name}
            </button>
          </div>
        ))}
      </div>

      {error && <p className="error-message">{error}</p>}

      <button
        onClick={handlePayment}
        disabled={!selectedPlan || loading} // Disable button if no plan is selected or if loading
      >
        {loading ? 'Processing...' : 'Proceed to PayPal'}
      </button>
    </div>
  );
};

export default PaymentPage;
