import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './EventPage.css';

const EventPage = () => {
  const [isEventSetup, setIsEventSetup] = useState(false); // Default to entering event ID/QR

  const handleToggleEventSetup = () => {
    setIsEventSetup(true);
  };

  const handleToggleEnterEvent = () => {
    setIsEventSetup(false);
  };

  return (
    <div className={`container ${isEventSetup ? 'active' : ''}`} id="container">
      <div className="form-container event-setup">
        <form id="event-setup-form" className="p-4">
          <h1 className="mb-4">Create Event</h1>
          <p className="mb-4">Enter event details to set up a new event</p>
          <input type="text" className="form-control mb-3" placeholder="Event Name" />
          <input type="date" className="form-control mb-3" placeholder="Event Date" />
          <button type="submit" className="btn btn-primary">Create Event</button>
        </form>
      </div>

      <div className="form-container enter-event">
        <form id="enter-event-form" className="p-4">
          <h1 className="mb-4">Find My Photo</h1>
          <p className="mb-4">Enter the event ID or scan QR code</p>
          <input type="text" className="form-control mb-3" placeholder="Event ID" />
          <input type="file" className="form-control mb-3" accept="image/*" />
          <button type="submit" className="btn btn-primary">Find Event</button>
        </form>
      </div>

      <div className="toggle-container">
        <div className="toggle">
          <div className="toggle-panel toggle-left">
            <h1>Already Have an Event?</h1>
            <p>Enter the event ID to participate</p>
            <button className="btn btn-secondary" id="enter-event" onClick={handleToggleEnterEvent}>
              Create Event
            </button>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Organizing an Event?</h1>
            <p>Set up your event and invite participants</p>
            <button className="btn btn-secondary" id="setup-event" onClick={handleToggleEventSetup}>
              Find My Photo
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventPage;
