// src/Components/Payment/PaymentPage.js
import React from 'react';
import axios from 'axios';
importPayment.css

const PaymentPage = () => {
  const plans = [
    {
      name: 'Skater',
      price: '16.58',
      storage: '100GB',
      galleryFeatures: [
        'Face Searches: Unlimited',
        'Registrations: Unlimited',
        'PicReco Domain',
        '1 Branding',
        'Limited PicReco Branding',
      ],
      notifications: [
        'Email Unlimited (from PicReco Domain)',
        'WhatsApp AddOn (from PicReco Number)',
        'WhatsApp Credits: 100 Included',
      ],
    },
    {
      name: 'Biker',
      price: '24.91',
      storage: '500GB',
      galleryFeatures: [
        'Face Searches: Unlimited',
        'Registrations: Unlimited',
        'Custom Sub-Domain',
        '1 Branding',
        'Limited PicReco Branding',
      ],
      notifications: [
        'Email Unlimited (from PicReco Domain)',
        'WhatsApp AddOn (from PicReco Number)',
        'WhatsApp Credits: 500 Included',
      ],
    },
    {
      name: 'Pilot',
      price: '31.58',
      storage: '1TB',
      galleryFeatures: [
        'Face Searches: Unlimited',
        'Registrations: Unlimited',
        'Your Own Domain',
        '2 Branding',
        'No PicReco Branding',
      ],
      notifications: [
        'Email Unlimited (from PicReco Domain)',
        'WhatsApp AddOn (from PicReco Number)',
        'WhatsApp Credits: 1000 Included',
      ],
    },
  ];

  const handleSelectPlan = async (planName, planPrice) => {
    try {
      const response = await axios.post('/api/payments/create-order', {
        planName,
        planPrice,
      });
      window.location.href = response.data.approvalUrl; // Redirect to payment gateway
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  return (
    <div className="payment-page">
      <h2>Upgrade Plan</h2>
      <div className="plans-contain">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p className="price">${plan.price} Per Month</p>
            <p className="storage">Storage: {plan.storage}</p>
            <div className="features">
              <h4>Gallery</h4>
              <ul>
                {plan.galleryFeatures.map((feature, i) => (
                  <li key={i}>{feature}</li>
                ))}
              </ul>
            </div>
            <div className="notifications">
              <h4>Notifications</h4>
              <ul>
                {plan.notifications.map((notification, i) => (
                  <li key={i}>{notification}</li>
                ))}
              </ul>
            </div>
            <button onClick={() => handleSelectPlan(plan.name, plan.price)}>Select</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PaymentPage;
