import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]); // All users fetched from the backend
  const [filteredUsers, setFilteredUsers] = useState([]); // Users filtered by paid photographers
  const [showPaidOnly, setShowPaidOnly] = useState(false); // Toggle for filtering paid photographers
  const [payments, setPayments] = useState([]); // Payments data fetched from the backend
  const navigate = useNavigate();
  const location = useLocation();

  // Effect to handle authentication and data fetching
  useEffect(() => {
    const authToken = sessionStorage.getItem('authToken'); // Get JWT token from session storage
    const userRole = sessionStorage.getItem('userRole');   // Get user role from session storage

    // If no token or user is not admin, redirect to login
    if (!authToken || userRole !== 'admin') {
      navigate('/auth');
    } else if (location.state === null && location.pathname === '/admin-dashboard') {
      // If the user manually types the URL, redirect to login if not authenticated
      navigate('/auth');
    } else {
      // Fetch users and payments once authenticated
      fetchUsers(authToken);
      fetchPayments(authToken);
    }
  }, [navigate, location]);

  // Fetch all users from backend
  const fetchUsers = async () => {
    try {
      const token = sessionStorage.getItem('authToken'); // Retrieve token from sessionStorage
      const response = await axios.get('http://localhost:5001/api/users/admin-data', {
        headers: { Authorization: `Bearer ${token}` }, // Pass token in Authorization header
      });
      console.log('Users data:', response.data.users); // Debug log
      setUsers(response.data.users); // Set users to state
    } catch (error) {
      console.error('Error fetching users:', error); // Log error if request fails
    }
  };

  useEffect(() => {
    fetchUsers(); // Call the function when the component mounts
  }, []);

  // Fetch payments from backend
  const fetchPayments = async (token) => {
    try {
      const response = await axios.get('http://localhost:5001/api/admin/payments', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPayments(response.data.payments); // Set payments state with fetched data
    } catch (error) {
      console.error('Error fetching payments', error);
    }
  };

  // Handle filter for showing only paid photographers
  const handleFilterChange = () => {
    setShowPaidOnly(!showPaidOnly);
    if (!showPaidOnly) {
      const paidPhotographers = users.filter(user => user.isPaid && user.role === 'photographer');
      setFilteredUsers(paidPhotographers); // Show only paid photographers
    } else {
      setFilteredUsers(users); // Show all users when filter is turned off
    }
  };

  // Handle logout: Remove token and role from session storage
  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('userRole');
    navigate('/auth'); // Redirect to login page after logout
  };

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>

      {/* Logout Button */}
      <button className="btn btn-dark" onClick={handleLogout}>Logout</button>

      {/* User List */}
      <div className="user-list">
        <h2>Users</h2>
        {/* Checkbox to filter only paid photographers */}
        <label>
          <input
            type="checkbox"
            checked={showPaidOnly}
            onChange={handleFilterChange}
          />
          Show Paid Photographers Only
        </label>
        <ul>
          {filteredUsers.map(user => (
            <li key={user._id}>
              {user.name} ({user.email}) - {user.role}
            </li>
          ))}
        </ul>
      </div>

      {/* Payment Tracking */}
      <div className="payment-tracking">
        <h2>Payment Tracking</h2>
        <ul>
          {payments.map(payment => (
            <li key={payment._id}>
              {payment.userEmail} - {payment.planName}: ${payment.planPrice} ({payment.status})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
