import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [showPaidOnly, setShowPaidOnly] = useState(false);
  const [payments, setPayments] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const authToken = sessionStorage.getItem('authToken');
    const userRole = sessionStorage.getItem('userRole');

    if (!authToken || userRole !== 'admin') {
      navigate('/auth');
    } else if (location.state === null && location.pathname === '/admin-dashboard') {
      navigate('/auth');
    } else {
      fetchUsers(authToken);
      fetchPayments(authToken);
    }
  }, [navigate, location]);

  const fetchUsers = async (token) => {
    try {
      const response = await axios.get('/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data.users);
      setFilteredUsers(response.data.users);
    } catch (error) {
      console.error('Error fetching users', error);
    }
  };

  const fetchPayments = async (token) => {
    try {
      const response = await axios.get('/api/admin/payments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPayments(response.data.payments);
    } catch (error) {
      console.error('Error fetching payments', error);
    }
  };

  const handleFilterChange = () => {
    setShowPaidOnly(!showPaidOnly);
    if (!showPaidOnly) {
      const paidPhotographers = users.filter(user => user.isPaid && user.role === 'photographer');
      setFilteredUsers(paidPhotographers);
    } else {
      setFilteredUsers(users);
    }
  };

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('userRole');
    navigate('/auth');
  };

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>

      <button className="btn btn-dark" onClick={handleLogout}>Logout</button>

      <div className="user-list">
        <h2>Users</h2>
        <label>
          <input
            type="checkbox"
            checked={showPaidOnly}
            onChange={handleFilterChange}
          />
          Show Paid Photographers Only
        </label>
        <ul>
          {filteredUsers.map(user => (
            <li key={user._id}>
              {user.name} ({user.email}) - {user.role}
            </li>
          ))}
        </ul>
      </div>

      <div className="payment-tracking">
        <h2>Payment Tracking</h2>
        <ul>
          {payments.map(payment => (
            <li key={payment._id}>
              {payment.userEmail} - {payment.planName}: ${payment.planPrice} ({payment.status})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
