import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Payment.css';

const PaymentPage = () => {
  const navigate = useNavigate();
  const [paymentMethod, setPaymentMethod] = useState('paypal'); // Default to PayPal
  const [selectedPlan, setSelectedPlan] = useState(null);

  const plans = [
    {
      name: 'pro',
      price: '16.58',
      storage: '100GB',
      planId: 'PLAN_ID_PRO', // PayPal subscription plan ID
    },
    {
      name: 'ultra',
      price: '24.91',
      storage: '500GB',
      planId: 'PLAN_ID_ULTRA',
    },
    {
      name: 'Promax',
      price: '31.58',
      storage: '1TB',
      planId: 'PLAN_ID_PROMAX',
    },
  ];

  const handleSelectPlan = (planId) => {
    setSelectedPlan(planId);
  };

  const handlePayment = async () => {
    if (!selectedPlan) {
      alert('Please select a plan');
      return;
    }

    if (paymentMethod === 'paypal') {
      // Handle PayPal payment
      try {
        const token = localStorage.getItem('authToken'); // Retrieve token from localStorage
        const response = await axios.post(
          'http://localhost:5001/api/payments/create-subscription',
          { planId: selectedPlan },
          {
            headers: {
              Authorization: `Bearer ${token}`, // Pass token in Authorization header
            },
          }
        );
        navigate(response.data.approvalUrl, { replace: true }); // Redirect to PayPal approval URL
      } catch (error) {
        console.error('Error creating subscription:', error);
      }
    } else if (paymentMethod === 'bank') {
      // Redirect to bank payment form or page
      navigate('/bank-payment-form', { replace: true });
    }
  };

  return (
    <div className="payment-page">
      <h2>Upgrade Plan</h2>
      <div className="plans-contain">
        {plans.map((plan, index) => (
          <div className="plan-card" key={index}>
            <h3>{plan.name}</h3>
            <p className="price">${plan.price} Per Month</p>
            <p className="storage">Storage: {plan.storage}</p>
            <button onClick={() => handleSelectPlan(plan.planId)}>
              Select {plan.name}
            </button>
          </div>
        ))}
      </div>

      <div className="payment-method">
        <h3>Select Payment Method</h3>
        <label>
          <input
            type="radio"
            value="paypal"
            checked={paymentMethod === 'paypal'}
            onChange={() => setPaymentMethod('paypal')}
          />
          PayPal
        </label>
        <label>
          <input
            type="radio"
            value="bank"
            checked={paymentMethod === 'bank'}
            onChange={() => setPaymentMethod('bank')}
          />
          Bank Transfer
        </label>
      </div>

      <button onClick={handlePayment}>Proceed to Payment</button>
    </div>
  );
};

export default PaymentPage;
